"""Tests for the sensor module."""
from unittest.mock import AsyncMock, MagicMock
from custom_components.eneco_price_rates.eneco_web import EnecoWeb


async def test_async_eneco_products(hass, aioclient_mock):
    web = EnecoWeb()
    web.fetch_page = AsyncMock(return_value=HTML_RESPONSE_10_22)
    info = await web.fetch_electricity_products()

    assert info.period == 'oktober 2022'

    products = info.products
    assert len(products) is 2
    assert products[0].name == "Zon en Wind Flex"
    assert products[0].pdf == "https://cdn.eneco.be/downloads/nl/general/tk/BC_032_012210_NL_ENECO_POWER_FLEX.pdf"
    assert products[1].name == "Zon en Wind Variabel"
    assert products[1].pdf == "https://cdn.eneco.be/downloads/nl/general/tk/BC_032_012210_NL_ENECO_POWER_VARIABLE.pdf"

